import React, { useState } from "react";

export default function ActivityForm() {
  const [calorieIntake, setCalorieIntake] = useState("");
  const [weight, setWeight] = useState("");
  const [formSaved, setFormSaved] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");
  const currentDate = getFormattedDate();

  function getFormattedDate() {
    const options = { year: "numeric", month: "long", day: "numeric" };
    return new Date().toLocaleDateString(undefined, options);
  }

  const handleSave = () => {
    if (!isValidInput(calorieIntake) || !isValidInput(weight)) {
      setErrorMessage("Please enter valid positive integers.");
      return;
    }

    console.log("Form data saved:", {
      date: getFormattedDate(),
      calorieIntake: parseInt(calorieIntake),
      weight: parseInt(weight),
    });
    setCalorieIntake("");
    setWeight("");
    setFormSaved(true);
    setErrorMessage("");
  };

  const handleReset = () => {
    setFormSaved(false);
  };

  const handleInputChange = (e, setValue) => {
    const inputValue = e.target.value;
    if (!isValidInput(inputValue)) {
      setErrorMessage("Please enter a valid positive integer.");
    } else {
      setErrorMessage("");
    }
    setValue(inputValue);
  };

  const isValidInput = (value) => {
    const intValue = parseInt(value);
    return !isNaN(intValue) && intValue > 0;
  };

  return (
    <div className="form-container">
      {formSaved ? (
        <div className="check-in-message">
          Check-in completed for {getFormattedDate()}
        </div>
      ) : (
        <form className="activity-form">
          <div className="title">{currentDate}</div>
          <label>
            Calorie Intake:
            <input
              type="text"
              value={calorieIntake}
              onChange={(e) => handleInputChange(e, setCalorieIntake)}
            />
          </label>

          <label>
            Weight:
            <input
              type="text"
              value={weight}
              onChange={(e) => handleInputChange(e, setWeight)}
            />
          </label>

          {errorMessage && <div className="error-message">{errorMessage}</div>}

          <button type="button" onClick={handleSave}>
            Save
          </button>
        </form>
      )}
      <div className="activity-form">
        {formSaved && (
          <button type="button" onClick={handleReset}>
            Edit Check-in
          </button>
        )}
      </div>
    </div>
  );
}
