import React, { useState } from "react";
import WorkoutModal from "../Components/Workout/WorkoutModal";
import WorkoutFilter from "../Components/Workout/WorkoutFilter";
import "./styles/Workouts.css";

function Workouts() {
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [selectedWorkout, setSelectedWorkout] = useState(null);
  const [filters, setFilters] = useState({
    equipment: "",
    difficulty: "",
    goal: "",
    muscle: "",
  });

  const openModal = (workout) => {
    setSelectedWorkout(workout);
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setSelectedWorkout(null);
    setModalIsOpen(false);
  };

  const applyFilters = (filterValues) => {
    setFilters(filterValues);
  };

const mockData = [
  {
    name: "Jogging",
    goal: "Weight Loss",
    equipment: "No Equipment",
    instructions: "Start with a brisk walk, then gradually increase your pace.",
    muscleTargetGroup: "Legs, Cardio",
    difficulty: "Easy",
    link: "https://example.com/jogging-workout-source",
  },
  {
    name: "Strength Training with Dumbbells",
    goal: "Muscle Gain",
    equipment: "Dumbbell",
    instructions:
      "Perform exercises such as bicep curls, tricep extensions, etc.",
    muscleTargetGroup: "Arms, Shoulders",
    difficulty: "Medium",
    link: "https://example.com/strength-training-source",
  },
  {
    name: "Pull-up Bar Circuit",
    goal: "Muscle Gain",
    equipment: "Pull-up Bar",
    instructions: "Perform pull-ups, chin-ups, and hanging leg raises.",
    muscleTargetGroup: "Back, Arms",
    difficulty: "Hard",
    link: "https://example.com/pull-up-circuit-source",
  },
  {
    name: "Yoga for Weight Loss",
    goal: "Weight Loss",
    equipment: "No Equipment",
    instructions: "Perform a series of yoga poses and breathing exercises.",
    muscleTargetGroup: "Full Body",
    difficulty: "Medium",
    link: "https://example.com/yoga-for-weight-loss-source",
  },
  {
    name: "Bodyweight Muscle Building",
    goal: "Muscle Gain",
    equipment: "No Equipment",
    instructions: "Perform exercises such as push-ups, squats, and lunges.",
    muscleTargetGroup: "Full Body",
    difficulty: "Medium",
    link: "https://example.com/bodyweight-muscle-building-source",
  },
  {
    name: "Pull-up Routine",
    goal: "Muscle Gain",
    equipment: "Pull-up Bar",
    instructions: "Perform pull-ups with different grips.",
    muscleTargetGroup: "Back, Arms",
    difficulty: "Hard",
    link: "https://example.com/pull-up-routine-source",
  },
];


  // Apply filters to workout data
  const filteredData = mockData.filter((workout) => {
    return (
      (filters.equipment === "" || workout.equipment === filters.equipment) &&
      (filters.difficulty === "" ||
        workout.difficulty === filters.difficulty) &&
      (filters.goal === "" || workout.goal === filters.goal) &&
      (filters.muscle === "" || workout.muscleTargetGroup === filters.muscle)
    );
  });

  return (
    <div className="Workout-page">
      <div className="header">
        <h1>Workouts</h1>
      </div>
      <div className="filter">
        {/* Pass applyFilters function and current filter values to WorkoutFilter */}
        <WorkoutFilter
          applyFilters={applyFilters}
          currentFilters={filters}
        ></WorkoutFilter>
      </div>
      <div className="list">
        {filteredData.map((workout, index) => (
          <div
            className="Workout-details"
            key={workout.name}
            onClick={() => openModal(workout)}
          >
            <p>{workout.name}</p>
            <p>Goal: {workout.goal}</p>
            <p>Equipment: {workout.equipment}</p>
          </div>
        ))}
      </div>

      <WorkoutModal
        isOpen={modalIsOpen}
        closeModal={closeModal}
        selectedWorkout={selectedWorkout}
      />
    </div>
  );
}

export default Workouts;
