import React, { useState } from "react";
import Modal from "react-modal"; // Import the Modal component
import WorkoutFilter from "../Components/Workout/WorkoutFilter";
import "./styles/Workouts.css";

function Workouts() {
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [selectedWorkout, setSelectedWorkout] = useState(null);

  const openModal = (workout) => {
    setSelectedWorkout(workout);
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setSelectedWorkout(null);
    setModalIsOpen(false);
  };

  const mockData = [
    // ... (your existing mockData)
  ];

  return (
    <div className="Workout-page">
      <div className="header">
        <h1>Workouts</h1>
      </div>
      <div className="filter">
        <WorkoutFilter></WorkoutFilter>
      </div>
      <div className="list">
        {mockData.map((workout, index) => (
          <div
            className="Workout-details"
            key={workout.name}
            onClick={() => openModal(workout)}
          >
            <p>{workout.name}</p>
            <p>Goal: {workout.goal}</p>
            <p>Equipment: {workout.equipment}</p>
          </div>
        ))}
      </div>

      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        contentLabel="Workout Details"
      >
        <h2>{selectedWorkout && selectedWorkout.name}</h2>
        <p>Goal: {selectedWorkout && selectedWorkout.goal}</p>
        <p>Equipment: {selectedWorkout && selectedWorkout.equipment}</p>
        <p>Instructions: {selectedWorkout && selectedWorkout.instructions}</p>
        <p>
          Muscle Target Group:{" "}
          {selectedWorkout && selectedWorkout.muscleTargetGroup}
        </p>
        <p>Difficulty: {selectedWorkout && selectedWorkout.difficulty}</p>
        <button onClick={closeModal}>Close</button>
      </Modal>
    </div>
  );
}

export default Workouts;
