import React, { useState, useEffect } from "react";

export default function SettingsForm() {
    const [userData, setUserData] = useState({
        firstName: '',
        lastName: '',
        email: '',
        role: 'client', // Assume 'client' as default if role is not provided
        // Add other fields as necessary
    });

    // Fetch user data from the database when the component mounts
    useEffect(() => {
        // Fetch the user details from the database using the user_id
        const userId = localStorage.getItem('userId');
        console.log("Userid in settings form is: ", userId);
        fetch(`/api/users/${userId}`)
            .then((response) => response.json())
            .then((data) => setUserData(data))
            .catch((error) => console.error('Error fetching user data:', error));
    }, []);

    const handleDeleteAccount = () => {
        // Logic to show confirmation modal for account deletion
        // ...
    };

    // Assuming the data is not editable, just display it
    return (
        <div className="settings-form">
            <div className="form-field">
                <label>First Name:</label>
                <span>{userData.firstName}</span>
            </div>
            <div className="form-field">
                <label>Last Name:</label>
                <span>{userData.lastName}</span>
            </div>
            <div className="form-field">
                <label>Email:</label>
                <span>{userData.email}</span>
            </div>
            <div className="form-field">
                <label>Role:</label>
                <span>{userData.role}</span>
            </div>

            <button onClick={handleDeleteAccount}>Delete Account</button>
        </div>
    );
}
