import React, { useState } from "react";
import WorkoutModal from "../Components/Workout/WorkoutModal";
import WorkoutFilter from "../Components/Workout/WorkoutFilter";
import "./styles/Workouts.css";

function Workouts() {
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [selectedWorkout, setSelectedWorkout] = useState(null);

  const [selectedEquipment, setSelectedEquipment] = useState("");
  const [selectedDifficulty, setSelectedDifficulty] = useState("");
  const [selectedGoal, setSelectedGoal] = useState("");
  const [selectedMuscle, setSelectedMuscle] = useState("");

  const handleApplyClick = () => {
    console.log("Selected Equipment:", selectedEquipment);
    console.log("Selected Difficulty:", selectedDifficulty);
    console.log("Selected Goal:", selectedGoal);
    console.log("Selected Muscle:", selectedMuscle);
  };

  const openModal = (workout) => {
    setSelectedWorkout(workout);
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setSelectedWorkout(null);
    setModalIsOpen(false);
  };

  return (
    <div className="Workout-page">
      <div className="header">
        <h1>Workouts</h1>
      </div>
      <div className="filter">
        <WorkoutFilter
          selectedEquipment={selectedEquipment}
          setSelectedEquipment={setSelectedEquipment}
          selectedDifficulty={selectedDifficulty}
          setSelectedDifficulty={setSelectedDifficulty}
          selectedGoal={selectedGoal}
          setSelectedGoal={setSelectedGoal}
          selectedMuscle={selectedMuscle}
          setSelectedMuscle={setSelectedMuscle}
          onApplyClick={handleApplyClick}
        />
      </div>
      <div className="list">
        {/* Use the filtered list based on the selected filters */}
        {mockData
          .filter(
            (workout) =>
              (!selectedEquipment || workout.equipment === selectedEquipment) &&
              (!selectedDifficulty || workout.difficulty === selectedDifficulty) &&
              (!selectedGoal || workout.goal === selectedGoal) &&
              (!selectedMuscle || workout.muscleTargetGroup.includes(selectedMuscle))
          )
          .map((workout, index) => (
            <div
              className="Workout-details"
              key={workout.name}
              onClick={() => openModal(workout)}
            >
              <p>{workout.name}</p>
              <p>Goal: {workout.goal}</p>
              <p>Equipment: {workout.equipment}</p>
            </div>
          ))}
      </div>

      <WorkoutModal
        isOpen={modalIsOpen}
        closeModal={closeModal}
        selectedWorkout={selectedWorkout}
      />
    </div>
  );
}

export default Workouts;
