import React, { useState, useEffect } from "react";

export default function SettingsForm() {
    const user = JSON.parse(localStorage.getItem("user"));
    const userID = user.user_id || localStorage.getItem('userId');
    const [surveyData, setSurveyData] = useState({});
    console.log("userID in SettingsForm.js ", userID);
    console.log("User in SettingsForm.js: ", user);

    useEffect(() => {
        const fetchSurveyData = async () => {
            try {
                const response = await fetch(`http://localhost:3001/surveyfetch/${userID}`);
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                const data = await response.json();
                console.log("Data from DB: ",data.surveyData);
                setSurveyData(data.surveyData[0]);
            } catch (error) {
                console.error('Error fetching survey data:', error);
            }
        };

        if (userID) {
            fetchSurveyData();
        }
    }, [userID]);

    const handleDeleteAccount = () => {
        // Logic to show confirmation modal for account deletion.
    };

    return (
        <div className="settings-content p-3">
      <div className="row">
        {/* User information display */}
        <div className="col-6">
          <p className="form-label">*First Name:</p>
          <p className="form-control-plaintext">{user.first_name}</p>
        </div>
        <div className="col-6">
          <p className="form-label">*Last Name:</p>
          <p className="form-control-plaintext">{user.last_name}</p>
        </div>
        {/* Repeat for other user fields */}

        {/* Survey information display */}
        <div className="col-6">
          <p className="form-label">Fitness Level:</p>
          <p className="form-control-plaintext">{surveyData.fitness_level}</p>
        </div>
        <div className="col-6">
          <p className="form-label">Diet Level:</p>
          <p className="form-control-plaintext">{surveyData.diet}</p>
        </div>
        {/* Repeat for other survey fields */}

        {/* Delete Account Button */}
        <div className="col-12">
          <button className="btn btn-danger mt-3" onClick={handleDeleteAccount}>Delete Account</button>
        </div>
      </div>
    </div>
    );
}
