import React, { useState } from "react";
import WorkoutModal from "../Components/Workout/WorkoutModal";
import WorkoutFilter from "../Components/Workout/WorkoutFilter";
import "./styles/Workouts.css";

function Workouts() {
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [selectedWorkout, setSelectedWorkout] = useState(null);
  const [filters, setFilters] = useState({
    equipment: "",
    difficulty: "",
    goal: "",
    muscle: "",
  });

  const openModal = (workout) => {
    setSelectedWorkout(workout);
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setSelectedWorkout(null);
    setModalIsOpen(false);
  };

  const applyFilters = (filterValues) => {
    setFilters(filterValues);
  };

  const mockData = [
    // ... your workout data ...
  ];

  // Apply filters to workout data
  const filteredData = mockData.filter((workout) => {
    return (
      (filters.equipment === "" || workout.equipment === filters.equipment) &&
      (filters.difficulty === "" || workout.difficulty === filters.difficulty) &&
      (filters.goal === "" || workout.goal === filters.goal) &&
      (filters.muscle === "" || workout.muscleTargetGroup === filters.muscle)
    );
  });

  return (
    <div className="Workout-page">
      <div className="header">
        <h1>Workouts</h1>
      </div>
      <div className="filter">
        {/* Pass applyFilters function and current filter values to WorkoutFilter */}
        <WorkoutFilter
          applyFilters={applyFilters}
          currentFilters={filters}
        ></WorkoutFilter>
      </div>
      <div className="list">
        {filteredData.map((workout, index) => (
          <div
            className="Workout-details"
            key={workout.name}
            onClick={() => openModal(workout)}
          >
            <p>{workout.name}</p>
            <p>Goal: {workout.goal}</p>
            <p>Equipment: {workout.equipment}</p>
          </div>
        ))}
      </div>

      <WorkoutModal
        isOpen={modalIsOpen}
        closeModal={closeModal}
        selectedWorkout={selectedWorkout}
      />
    </div>
  );
}

export default Workouts;
